(function (global, factory) {
  if (typeof define === "function" && define.amd) {
    define(["@oasis-engine/controls", "oasis-engine"], factory);
  } else if (typeof exports !== "undefined") {
    factory(require("@oasis-engine/controls"), require("oasis-engine"));
  } else {
    var mod = {
      exports: {}
    };
    factory(global["@oasisEngine/controls"], global.oasisEngine);
    global.index = mod.exports;
  }
})(typeof globalThis !== "undefined" ? globalThis : typeof self !== "undefined" ? self : this, function (_controls, _oasisEngine) {
  "use strict";

  /**
   * @title Animation
   * @category Benchmark
   */
  // Create engine object.
  var engine = new _oasisEngine.WebGLEngine("canvas");
  engine.canvas.resizeByClientSize(); // Create root entity and get scene.

  var scene = engine.sceneManager.activeScene;
  var rootEntity = scene.createRootEntity();
  scene.ambientLight.diffuseSolidColor.setValue(1, 1, 1, 1); // Create camera.

  var cameraEntity = rootEntity.createChild("Camera");
  cameraEntity.transform.setPosition(0, 0, 12);
  cameraEntity.addComponent(_oasisEngine.Camera);
  cameraEntity.addComponent(_controls.OrbitControl); // Load resources and add modles.

  engine.resourceManager.load([{
    url: "https://gw.alipayobjects.com/os/loanprod/bf055064-3eec-4d40-bce0-ddf11dfbb88a/5d78db60f211d21a43834e23/4f5e6bb277dd2fab8e2097d7a418c5bc.gltf",
    type: _oasisEngine.AssetType.Perfab
  }, {
    url: "https://gw.alipayobjects.com/mdn/rms_7c464e/afts/img/A*OStMT63k5o8AAAAAAAAAAAAAARQnAQ",
    type: _oasisEngine.AssetType.Texture2D
  }]).then(function (reources) {
    var gltf = reources[0];
    var baseTexture = reources[1];
    var huabei = gltf.defaultSceneRoot;
    gltf.materials.forEach(function (material) {
      material.baseTexture = baseTexture;
      material.baseColor.setValue(1, 1, 1, 1);
    });

    for (var i = 0; i < 8; i++) {
      for (var j = 0; j < 8; j++) {
        var huabeiClone = huabei.clone();
        rootEntity.addChild(huabeiClone);
        var transform = huabeiClone.transform;
        transform.setRotation(0, -90, 0);
        transform.setScale(0.5, 0.5, 0.5);
        transform.setPosition(i * 1.0 - 3.0, j * 1.2, -j * 3.5);
        huabeiClone.getComponent(_oasisEngine.Animation).playAnimationClip("A");
      }
    }
  }); // Run engine

  engine.run();
});
