(function (global, factory) {
  if (typeof define === "function" && define.amd) {
    define(["dat.gui", "oasis-engine", "@oasis-engine/controls"], factory);
  } else if (typeof exports !== "undefined") {
    factory(require("dat.gui"), require("oasis-engine"), require("@oasis-engine/controls"));
  } else {
    var mod = {
      exports: {}
    };
    factory(global.dat, global.oasisEngine, global["@oasisEngine/controls"]);
    global.index = mod.exports;
  }
})(typeof globalThis !== "undefined" ? globalThis : typeof self !== "undefined" ? self : this, function (_dat, _oasisEngine, _controls) {
  "use strict";

  /**
   * @title Orthographic Camera
   * @category Camera
   */
  var engine = new _oasisEngine.WebGLEngine("canvas");
  engine.canvas.resizeByClientSize();
  var rootEntity = engine.sceneManager.activeScene.createRootEntity(); // init camera

  var cameraEntity = rootEntity.createChild("camera");
  var camera = cameraEntity.addComponent(_oasisEngine.Camera);
  camera.nearClipPlane = 0.1;
  cameraEntity.transform.setPosition(10, 2, 10);
  cameraEntity.transform.lookAt(new _oasisEngine.Vector3(0, 0, 0));
  cameraEntity.addComponent(_controls.OrbitControl);
  var lightEntity = rootEntity.createChild("Light");
  lightEntity.transform.setRotation(-30, 0, 0);
  lightEntity.addComponent(_oasisEngine.DirectLight); // init cube

  createCube(0, 0, 0);
  createCube(6.5, 0, 6.5);
  createCube(-6.5, 0, -6.5);
  addGUI();
  engine.run();

  function createCube(x, y, z) {
    var cubeEntity = rootEntity.createChild("cube");
    var renderer = cubeEntity.addComponent(_oasisEngine.MeshRenderer);
    var material = new _oasisEngine.BlinnPhongMaterial(engine);
    cubeEntity.transform.setPosition(x, y, z);
    material.baseColor = new _oasisEngine.Color(1, 0.25, 0.25, 1);
    renderer.setMaterial(material);
    renderer.mesh = _oasisEngine.PrimitiveMesh.createCuboid(engine, 1, 1, 1);
  }

  function addGUI() {
    var gui = new _dat.GUI();
    var cameraFolder = gui.addFolder("camera orthogonal switch");
    cameraFolder.open();
    camera.isOrthographic = true;
    cameraFolder.add(camera, "isOrthographic");
  }
});
