/**
 * @title AmbientLight
 * @category Light
 */
import { OrbitControl } from "@oasis-engine/controls";
import * as dat from "dat.gui";
import {
  AmbientLight,
  AssetType,
  BackgroundMode,
  Camera,
  DiffuseMode,
  DirectLight,
  Logger,
  MeshRenderer,
  PBRMaterial,
  PrimitiveMesh,
  SkyBoxMaterial,
  Vector3,
  WebGLEngine
} from "oasis-engine";
Logger.enable();
const engine = new WebGLEngine("canvas");
engine.canvas.resizeByClientSize();

const scene = engine.sceneManager.activeScene;
const rootEntity = scene.createRootEntity();
const gui = new dat.GUI();

// Create camera
const cameraNode = rootEntity.createChild("camera_node");
cameraNode.transform.position = new Vector3(-3, 0, 3);
cameraNode.addComponent(Camera);
cameraNode.addComponent(OrbitControl);

// Create sky
const sky = scene.background.sky;
const skyMaterial = new SkyBoxMaterial(engine);
scene.background.mode = BackgroundMode.Sky;
sky.material = skyMaterial;
sky.mesh = PrimitiveMesh.createCuboid(engine, 1, 1, 1);

const lightEntity = rootEntity.createChild();
lightEntity.addComponent(DirectLight).intensity = 0.5;
lightEntity.transform.setPosition(5, 5, 5);
lightEntity.transform.lookAt(new Vector3(0, 0, 0));

// material ball
const ball = rootEntity.createChild("ball");
const ballRender = ball.addComponent(MeshRenderer);
const material = new PBRMaterial(engine);
material.metallic = 0;
material.roughness = 0;
ballRender.mesh = PrimitiveMesh.createSphere(engine, 1, 64);
ballRender.setMaterial(material);

engine.resourceManager
  .load<AmbientLight>({
    type: AssetType.Env,
    url: "https://gw.alipayobjects.com/os/bmw-prod/258a783d-0673-4b47-907a-da17b882feee.bin"
  })
  .then((ambientLight) => {
    scene.ambientLight = ambientLight;
    skyMaterial.textureCubeMap = ambientLight.specularTexture;
    skyMaterial.textureDecodeRGBM = true;
    openDebug(ambientLight.specularTexture);
    engine.run();
  });

function openDebug(specularTexture) {
  const info = {
    diffuseMode: "SphericalHarmonics",
    diffuseSolidColor: [0.212 * 255, 0.227 * 255, 0.259 * 255],
    specularTexture: true
  };

  gui.add(info, "diffuseMode", ["SolidColor", "SphericalHarmonics"]).onChange((v) => {
    if (v === "SphericalHarmonics") {
      scene.ambientLight.diffuseMode = DiffuseMode.SphericalHarmonics;
    } else if (v === "SolidColor") {
      scene.ambientLight.diffuseMode = DiffuseMode.SolidColor;
    }
  });

  gui.addColor(info, "diffuseSolidColor").onChange((v) => {
    scene.ambientLight.diffuseSolidColor.setValue(v[0] / 255, v[1] / 255, v[2] / 255, 1);
  });

  gui.add(info, "specularTexture").onChange((v) => {
    if (v) {
      scene.ambientLight.specularTexture = specularTexture;
    } else {
      scene.ambientLight.specularTexture = null;
    }
  });

  // env light debug
  gui.add(scene.ambientLight, "specularIntensity", 0, 1);
  gui.add(scene.ambientLight, "diffuseIntensity", 0, 1);
}

/**
 * @title Light Type
 * @category Light
 */
import { OrbitControl } from "@oasis-engine/controls";
import * as dat from "dat.gui";
import {
  BlinnPhongMaterial,
  Camera,
  DirectLight,
  MeshRenderer,
  PointLight,
  PrimitiveMesh,
  RenderFace,
  SpotLight,
  UnlitMaterial,
  Vector3,
  WebGLEngine
} from "oasis-engine";
const gui = new dat.GUI();

// Create engine object
const engine = new WebGLEngine("canvas");
engine.canvas.resizeByClientSize();

const scene = engine.sceneManager.activeScene;
const rootEntity = scene.createRootEntity();

// Create Ground
const groundEntity = rootEntity.createChild("ground");
const groundRenderer = groundEntity.addComponent(MeshRenderer);
const mesh = PrimitiveMesh.createPlane(engine, 100, 100);
const material = new BlinnPhongMaterial(engine);

material.renderFace = RenderFace.Double;
groundRenderer.mesh = mesh;
groundRenderer.setMaterial(material);

// Create camera
const cameraNode = rootEntity.createChild("camera_node");
cameraNode.transform.setPosition(0, 30, 50);
const camera = cameraNode.addComponent(Camera);
camera.farClipPlane = 200;

const control = cameraNode.addComponent(OrbitControl);
control.maxDistance = 100;

// Create spot light
const lightEntity = rootEntity.createChild("light");
const spotLight = lightEntity.addComponent(SpotLight);
const pointLight = lightEntity.addComponent(PointLight);
const directionalLight = lightEntity.addComponent(DirectLight);
const target = new Vector3(0, 0, 0);
const up = new Vector3(0, 1, 0);

let light: any = spotLight;
pointLight.enabled = false;
directionalLight.enabled = false;

lightEntity.transform.setPosition(20, 20, 0);
lightEntity.transform.lookAt(target, up);

const lightRenderer = lightEntity.addComponent(MeshRenderer);
lightRenderer.mesh = PrimitiveMesh.createSphere(engine, 1);
lightRenderer.setMaterial(new UnlitMaterial(engine));

// Debug
const debugInfo = {
  type: "SpotLight",
  angle: 30,
  penumbra: 15,
  x: 20,
  y: 20,
  z: 0
};

gui.add(debugInfo, "type", ["DirectionalLight", "PointLight", "SpotLight"]).onChange((v) => {
  light.enabled = false;
  spotFolder.closed = true;
  switch (v) {
    case "SpotLight":
      light = spotLight;
      light.enabled = true;
      spotFolder.closed = false;
      break;
    case "DirectionalLight":
      light = directionalLight;
      light.enabled = true;
      break;
    case "PointLight":
      light = pointLight;
      light.enabled = true;
      break;
  }
});
gui.add(light, "distance", 0, 100, 1);

const folder = gui.addFolder("位置");
folder.open();
folder.add(debugInfo, "x", -100, 100).onChange((v) => {
  const last = lightEntity.transform.position;
  lightEntity.transform.setPosition(v, last.y, last.z);
  lightEntity.transform.lookAt(target, up);
});
folder.add(debugInfo, "y", 0, 100).onChange((v) => {
  const last = lightEntity.transform.position;
  lightEntity.transform.setPosition(last.x, v, last.z);
  lightEntity.transform.lookAt(target, up);
});
folder.add(debugInfo, "z", -100, 100).onChange((v) => {
  const last = lightEntity.transform.position;
  lightEntity.transform.setPosition(last.x, last.y, v);
  lightEntity.transform.lookAt(target, up);
});

const spotFolder = gui.addFolder("SpotLight");
spotFolder.open();

spotFolder.add(debugInfo, "angle", 1, 90, 1).onChange((v) => {
  light.angle = (v * Math.PI) / 180;
});
spotFolder.add(debugInfo, "penumbra", 1, 90, 1).onChange((v) => {
  light.penumbra = (v * Math.PI) / 180;
});

// Run
engine.run();
