/**
 * @title Text Renderer
 * @category 2D
 */

import { OrbitControl } from "@oasis-engine/controls";
import { Camera, Color, Font, FontStyle, TextRenderer, Vector3, WebGLEngine } from "oasis-engine";

// Create engine object
const engine = new WebGLEngine("canvas");
engine.canvas.resizeByClientSize();

const scene = engine.sceneManager.activeScene;
const rootEntity = scene.createRootEntity();

// Create camera
const cameraEntity = rootEntity.createChild("camera_entity");
cameraEntity.transform.setPosition(0, 0, 10);
cameraEntity.addComponent(Camera);
cameraEntity.addComponent(OrbitControl);

// The text to display
const text = "Oasis 文字系统来啦～";
// The position of text
const pos = new Vector3();
// The color of text
const color = new Color();

// Create text with default params
pos.setValue(0, 1.25, 0);
color.setValue(1, 1, 1, 1);
createText();
// Create text with cursive font family
pos.setValue(0, 0.75, 0);
color.setValue(1, 1, 1, 1);
createText("cursive");
// Create text with font size 36
pos.setValue(0, 0.25, 0);
color.setValue(1, 0.5, 0.5, 1);
createText("Arial", 36);
// Create text with bold
pos.setValue(0, -0.25, 0);
color.setValue(1, 1, 1, 1);
createText("Arial", 26, true);
// Create text with italic
pos.setValue(0, -0.75, 0);
color.setValue(1, 1, 1, 1);
createText("Arial", 26, false, true);
// Create text with bold and italic
pos.setValue(0, -1.25, 0);
color.setValue(1, 1, 1, 1);
createText("Arial", 26, true, true);

engine.run();

/**
 * Create text to display by params.
 * @param fontFamily - The font family
 * @param fontSize - The size of font
 * @param bold - The text whether bold
 * @param italic - The text whether italic
 */
function createText(
  fontFamily: string = "Arial",
  fontSize: number = 26,
  bold: boolean = false,
  italic: boolean = false
): void {
  // Create text entity
  const entity = rootEntity.createChild("text");
  entity.transform.position = pos;
  // Add text renderer for text entity
  const renderer = entity.addComponent(TextRenderer);
  // Set text color
  renderer.color = color;
  // Set text to render
  renderer.text = text;
  // Set font with font family
  renderer.font = Font.createFromOS(entity.engine, fontFamily);
  // Set font size
  renderer.fontSize = fontSize;
  // Set font whether bold
  bold && (renderer.fontStyle |= FontStyle.Bold);
  // Set font whether italic
  italic && (renderer.fontStyle |= FontStyle.Italic);
}

/**
 * @title Text Wrap And Alignment
 * @category 2D
 */

import { OrbitControl } from "@oasis-engine/controls";
import { Camera, Entity, TextHorizontalAlignment, TextRenderer, WebGLEngine } from "oasis-engine";

// Create engine object
const engine = new WebGLEngine("canvas");
engine.canvas.resizeByClientSize();

const scene = engine.sceneManager.activeScene;
const rootEntity = scene.createRootEntity();

// Create camera
const cameraEntity = rootEntity.createChild("camera_entity");
cameraEntity.transform.setPosition(0, 0, 10);
cameraEntity.addComponent(Camera);
cameraEntity.addComponent(OrbitControl);

// The text to display
const text = "文字折行对齐示例，根据设置的宽自动换行";
const textEntity = rootEntity.createChild("text");
textEntity.addComponent(TextRenderer);

// Text display no wrap, center align
setTextRenderer(textEntity, 2, `${text} (不换行，居中对齐)`, 2, false, TextHorizontalAlignment.Center);
// Text display with wrap, center align
setTextRenderer(textEntity.clone(), 1, `${text}\n(换行，居中对齐)`, 2, true, TextHorizontalAlignment.Center);
// Text display with wrap, left align
setTextRenderer(textEntity.clone(), 0, `${text}\n(换行，左对齐)`, 2, true, TextHorizontalAlignment.Left);
// Text display with wrap, right align
setTextRenderer(textEntity.clone(), -1, `${text}\n(换行，右对齐)`, 2, true, TextHorizontalAlignment.Right);

engine.run();

function setTextRenderer(
  entity: Entity,
  posY: number,
  text: string,
  width: number,
  wrap: boolean,
  hAlign: TextHorizontalAlignment
): void {
  rootEntity.addChild(entity);
  entity.transform.position.y = posY;
  // Get the text renderer
  const renderer = entity.getComponent(TextRenderer);
  renderer.text = text;
  renderer.width = width;
  // Set whether wrap
  renderer.enableWrapping = wrap;
  // Set horizontal alignment
  renderer.horizontalAlignment = hAlign;
}
