(function (global, factory) {
  if (typeof define === "function" && define.amd) {
    define(["oasis-engine", "@oasis-engine/controls", "@oasis-engine/physics-lite"], factory);
  } else if (typeof exports !== "undefined") {
    factory(require("oasis-engine"), require("@oasis-engine/controls"), require("@oasis-engine/physics-lite"));
  } else {
