})(typeof globalThis !== "undefined" ? globalThis : typeof self !== "undefined" ? self : this, function (_oasisEngine, _controls, _physicsLite) {
  "use strict";

  /**
   * @title Lite Raycast
   * @category Physics
   */
  var engine = new _oasisEngine.WebGLEngine("canvas");
  engine.physicsManager.initialize(_physicsLite.LitePhysics);
  engine.canvas.resizeByClientSize();
  var scene = engine.sceneManager.activeScene;
  var rootEntity = scene.createRootEntity("root");
  scene.ambientLight.diffuseSolidColor.setValue(1, 1, 1, 1);
  scene.ambientLight.diffuseIntensity = 1.2; // init camera

  var cameraEntity = rootEntity.createChild("camera");
  var camera = cameraEntity.addComponent(_oasisEngine.Camera);
  cameraEntity.transform.setPosition(10, 10, 10);
  cameraEntity.addComponent(_controls.OrbitControl); // init point light

  var lightEntity = rootEntity.createChild("light");
  lightEntity.transform.setPosition(0, 3, 0);
  var pointLight = lightEntity.addComponent(_oasisEngine.PointLight);
  pointLight.intensity = 0.3; // create sphere test entity

  var radius = 1.25;
  var sphereEntity = rootEntity.createChild("SphereEntity");
  sphereEntity.transform.setPosition(-3, 0, 0);
  var sphereMtl = new _oasisEngine.BlinnPhongMaterial(engine);
  var sphereRenderer = sphereEntity.addComponent(_oasisEngine.MeshRenderer);
  sphereMtl.baseColor.setValue(0.7, 0.1, 0.1, 1.0);
  sphereRenderer.mesh = _oasisEngine.PrimitiveMesh.createSphere(engine, radius);
  sphereRenderer.setMaterial(sphereMtl);
  var sphereCollider = sphereEntity.addComponent(_oasisEngine.StaticCollider);
  var sphereColliderShape = new _oasisEngine.SphereColliderShape();
  sphereColliderShape.radius = radius;
  sphereCollider.addShape(sphereColliderShape); // create box test entity

  var cubeSize = 2.0;
  var boxEntity = rootEntity.createChild("BoxEntity");
  var boxMtl = new _oasisEngine.BlinnPhongMaterial(engine);
  var boxRenderer = boxEntity.addComponent(_oasisEngine.MeshRenderer);
  boxMtl.baseColor.setValue(0.1, 0.7, 0.1, 1.0);
  boxRenderer.mesh = _oasisEngine.PrimitiveMesh.createCuboid(engine, cubeSize, cubeSize, cubeSize);
  boxRenderer.setMaterial(boxMtl);
  var boxCollider = boxEntity.addComponent(_oasisEngine.StaticCollider);
  var boxColliderShape = new _oasisEngine.BoxColliderShape();
  boxColliderShape.setSize(cubeSize, cubeSize, cubeSize);
  boxCollider.addShape(boxColliderShape); // raycast logic

  var pickedMeshRenderer;
  var originalColor = new _oasisEngine.Color();
  var point = new _oasisEngine.Vector2();
  var ray = new _oasisEngine.Ray();
  var hit = new _oasisEngine.HitResult();
  window.addEventListener("mousedown", function (event) {
    point.setValue(event.pageX * window.devicePixelRatio, event.pageY * window.devicePixelRatio);
    camera.screenPointToRay(point, ray);
    var result = engine.physicsManager.raycast(ray, Number.MAX_VALUE, _oasisEngine.Layer.Everything, hit);

    if (result) {
      pickedMeshRenderer = hit.entity.getComponent(_oasisEngine.MeshRenderer);
      var material = pickedMeshRenderer.getMaterial();
      material.baseColor.cloneTo(originalColor);
      material.baseColor.setValue(0.3, 0.3, 0.3, 1.0);
    }
  });
  window.addEventListener("mouseup", function () {
    pickedMeshRenderer.getMaterial().baseColor = originalColor;
  }); // Run engine

  engine.run();
});
