})(typeof globalThis !== "undefined" ? globalThis : typeof self !== "undefined" ? self : this, function (_controls, _oasisEngine) {
  "use strict";

  /**
   * @title Text Wrap And Alignment
   * @category 2D
   */
  // Create engine object
  var engine = new _oasisEngine.WebGLEngine("canvas");
  engine.canvas.resizeByClientSize();
  var scene = engine.sceneManager.activeScene;
  var rootEntity = scene.createRootEntity(); // Create camera

  var cameraEntity = rootEntity.createChild("camera_entity");
  cameraEntity.transform.setPosition(0, 0, 10);
  cameraEntity.addComponent(_oasisEngine.Camera);
  cameraEntity.addComponent(_controls.OrbitControl); // The text to display

  var text = "文字折行对齐示例，根据设置的宽自动换行";
  var textEntity = rootEntity.createChild("text");
  textEntity.addComponent(_oasisEngine.TextRenderer); // Text display no wrap, center align

  setTextRenderer(textEntity, 2, text + " (\u4E0D\u6362\u884C\uFF0C\u5C45\u4E2D\u5BF9\u9F50)", 2, false, _oasisEngine.TextHorizontalAlignment.Center); // Text display with wrap, center align

  setTextRenderer(textEntity.clone(), 1, text + "\n(\u6362\u884C\uFF0C\u5C45\u4E2D\u5BF9\u9F50)", 2, true, _oasisEngine.TextHorizontalAlignment.Center); // Text display with wrap, left align

  setTextRenderer(textEntity.clone(), 0, text + "\n(\u6362\u884C\uFF0C\u5DE6\u5BF9\u9F50)", 2, true, _oasisEngine.TextHorizontalAlignment.Left); // Text display with wrap, right align

  setTextRenderer(textEntity.clone(), -1, text + "\n(\u6362\u884C\uFF0C\u53F3\u5BF9\u9F50)", 2, true, _oasisEngine.TextHorizontalAlignment.Right);
  engine.run();

  function setTextRenderer(entity, posY, text, width, wrap, hAlign) {
    rootEntity.addChild(entity);
    entity.transform.position.y = posY; // Get the text renderer

    var renderer = entity.getComponent(_oasisEngine.TextRenderer);
    renderer.text = text;
    renderer.width = width; // Set whether wrap

    renderer.enableWrapping = wrap; // Set horizontal alignment

    renderer.horizontalAlignment = hAlign;
  }
