/**
 * @title GLTF Basic
 * @category Basic
 */
import { OrbitControl } from "@oasis-engine-toolkit/controls";
import { Camera, GLTFResource, WebGLEngine } from "oasis-engine";

const engine = new WebGLEngine("canvas");
engine.canvas.resizeByClientSize();

const rootEntity = engine.sceneManager.activeScene.createRootEntity();

const cameraEntity = rootEntity.createChild("camera");
cameraEntity.addComponent(Camera);
cameraEntity.transform.setPosition(3, 3, 3);
cameraEntity.addComponent(OrbitControl);

engine.sceneManager.activeScene.ambientLight.diffuseSolidColor.set(1, 1, 1, 1);

engine.resourceManager
  .load<GLTFResource>("https://gw.alipayobjects.com/os/OasisHub/267000040/9994/%25E5%25BD%2592%25E6%25A1%25A3.gltf")
  .then((gltf) => {
    rootEntity.addChild(gltf.defaultSceneRoot);
  });

engine.run();

/**
 * @title Free Controls
 * @category Camera
 */
/**
 * 本示例展示如何使用几何体渲染器功能、如何创建几何体资源对象、如何创建材质对象
 */
import {
  BlinnPhongMaterial,
  Camera,
  DirectLight,
  MeshRenderer,
  MeshTopology,
  PrimitiveMesh,
  WebGLEngine
} from "oasis-engine";
import { FreeControl } from "oasis-engine-toolkit";

const engine = new WebGLEngine("canvas");
engine.canvas.resizeByClientSize();
const scene = engine.sceneManager.activeScene;
const rootNode = scene.createRootEntity("root");

// 在场景中创建相机节点、配置位置和目标方向
const cameraNode = rootNode.createChild("Camera");
cameraNode.transform.setPosition(0, 0, 20);
const camera = cameraNode.addComponent(Camera);
const controler = cameraNode.addComponent(FreeControl);
camera.farClipPlane = 2000;
controler.movementSpeed = 100;
controler.rotateSpeed = 1;

const lightNode = rootNode.createChild("Light");
lightNode.transform.setRotation(-45, 45, 0);
lightNode.addComponent(DirectLight);

const cuboid = PrimitiveMesh.createCuboid(engine, 50, 50, 50);
const material = new BlinnPhongMaterial(engine);

const groundGeometry = PrimitiveMesh.createPlane(engine, 2000, 2000, 100, 100);
groundGeometry.subMesh.topology = MeshTopology.LineStrip;
const groundMaterial = new BlinnPhongMaterial(engine);

// create meshes in scene
for (let i = 0; i < 100; i++) {
  let cube = rootNode.createChild("cube");
  cube.transform.setPosition(Math.random() * 2000 - 1000, Math.random() * 200, Math.random() * 2000 - 1000);
  const cubeRenderer = cube.addComponent(MeshRenderer);
  cubeRenderer.mesh = cuboid;
  cubeRenderer.setMaterial(material);
}

// Ground
const ground = rootNode.createChild("ground");
ground.transform.setPosition(0, -25, 0);
ground.transform.rotate(-90, 0, 0);
const groundRender = ground.addComponent(MeshRenderer);
groundRender.mesh = groundGeometry;
groundRender.setMaterial(groundMaterial);

// Run engine
engine.run();

/**
 * 本示例展示如何使用几何体渲染器功能、如何创建几何体资源对象、如何创建材质对象
 */

/**
 * @title Ortho Controls
 * @category Camera
 */
import { AssetType, Camera, Sprite, SpriteRenderer, Texture2D, WebGLEngine } from "oasis-engine";
import { OrthoControl } from "oasis-engine-toolkit";

// Create engine object
const engine = new WebGLEngine("canvas");
engine.canvas.resizeByClientSize();

const scene = engine.sceneManager.activeScene;
const rootEntity = scene.createRootEntity();

// Create camera
const cameraEntity = rootEntity.createChild("Camera");
cameraEntity.transform.setPosition(0, 0, 50);
const camera = cameraEntity.addComponent(Camera);
camera.isOrthographic = true;

// Add camera control.
const cameraControl = cameraEntity.addComponent(OrthoControl);
const mainElement = engine.canvas._webCanvas;
mainElement.addEventListener(
  "wheel",
  (e) => {
    // @ts-ignore
    if (e.deltaY < 0) {
      cameraControl.zoomIn();
    } else {
      cameraControl.zoomOut();
    }
  },
  false
);
mainElement.addEventListener("mousedown", (e) => {
  // @ts-ignore
  cameraControl.panStart(e.clientX, e.clientY);
});
mainElement.addEventListener("mousemove", (e) => {
  // @ts-ignore
  cameraControl.panMove(e.clientX, e.clientY);
});
mainElement.addEventListener("mouseup", (e) => {
  // @ts-ignore
  cameraControl.panEnd();
});

engine.resourceManager
  .load<Texture2D>({
    url: "https://gw.alipayobjects.com/mdn/rms_7c464e/afts/img/A*KjnzTpE8LdAAAAAAAAAAAAAAARQnAQ",
    type: AssetType.Texture2D
  })
  .then((texture) => {
    // Create sprite entity.
    const spriteEntity = rootEntity.createChild("sprite");
    const spriteRenderer = spriteEntity.addComponent(SpriteRenderer);
    spriteRenderer.sprite = new Sprite(engine, texture);
  });

engine.run();
