/**
 * @title Scene Background
 * @category Scene
 */
import * as dat from "dat.gui";
import {
  AssetType,
  BackgroundMode,
  Camera,
  PrimitiveMesh,
  SkyBoxMaterial,
  TextureCube,
  WebGLEngine
} from "oasis-engine";

import { OrbitControl } from "@oasis-engine-toolkit/controls";

const engine = new WebGLEngine("canvas");
engine.canvas.resizeByClientSize();
const scene = engine.sceneManager.activeScene;
const { background } = scene;
const root = scene.createRootEntity();
const cameraEntity = root.createChild();
cameraEntity.transform.setPosition(0, 0, 10);
cameraEntity.addComponent(Camera);
cameraEntity.addComponent(OrbitControl);

engine.run();

engine.canvas._webCanvas.addEventListener("onresize", () => {
  engine.canvas.resizeByClientSize();
});

engine.resourceManager
  //@ts-ignore
  .load<[TextureCube, TextureCube, Texture2D]>([
    {
      urls: [
        "https://gw.alipayobjects.com/mdn/rms_7c464e/afts/img/A*5w6_Rr6ML6IAAAAAAAAAAAAAARQnAQ",
        "https://gw.alipayobjects.com/mdn/rms_7c464e/afts/img/A*TiT2TbN5cG4AAAAAAAAAAAAAARQnAQ",
        "https://gw.alipayobjects.com/mdn/rms_7c464e/afts/img/A*8GF6Q4LZefUAAAAAAAAAAAAAARQnAQ",
        "https://gw.alipayobjects.com/mdn/rms_7c464e/afts/img/A*D5pdRqUHC3IAAAAAAAAAAAAAARQnAQ",
        "https://gw.alipayobjects.com/mdn/rms_7c464e/afts/img/A*_FooTIp6pNIAAAAAAAAAAAAAARQnAQ",
        "https://gw.alipayobjects.com/mdn/rms_7c464e/afts/img/A*CYGZR7ogZfoAAAAAAAAAAAAAARQnAQ"
      ],
      type: AssetType.TextureCube
    },
    {
      urls: [
        "https://gw.alipayobjects.com/mdn/rms_7c464e/afts/img/A*Bk5FQKGOir4AAAAAAAAAAAAAARQnAQ",
        "https://gw.alipayobjects.com/mdn/rms_7c464e/afts/img/A*_cPhR7JMDjkAAAAAAAAAAAAAARQnAQ",
        "https://gw.alipayobjects.com/mdn/rms_7c464e/afts/img/A*trqjQp1nOMQAAAAAAAAAAAAAARQnAQ",
        "https://gw.alipayobjects.com/mdn/rms_7c464e/afts/img/A*_RXwRqwMK3EAAAAAAAAAAAAAARQnAQ",
        "https://gw.alipayobjects.com/mdn/rms_7c464e/afts/img/A*q4Q6TroyuXcAAAAAAAAAAAAAARQnAQ",
        "https://gw.alipayobjects.com/mdn/rms_7c464e/afts/img/A*DP5QTbTSAYgAAAAAAAAAAAAAARQnAQ"
      ],
      type: AssetType.TextureCube
    },
    {
      url: "https://gw.alipayobjects.com/mdn/rms_2e421e/afts/img/A*BcWiRYM7hroAAAAAAAAAAAAAARQnAQ",
      type: AssetType.Texture2D
    }
  ])
  .then(([cubeMap1, cubeMap2, texture]) => {
    // 添加天空盒背景
    // background.mode = BackgroundMode.Sky; // 默认纯色背景
    const skyMaterial = (background.sky.material = new SkyBoxMaterial(engine)); // 添加天空盒材质
    skyMaterial.textureCubeMap = cubeMap1; // 设置立方体纹理
    background.sky.mesh = PrimitiveMesh.createCuboid(engine, 2, 2, 2); // 设置天空盒网格
    background.texture = texture;
    return [cubeMap1, cubeMap2];
  })
  .then((cubeMaps) => {
    addGUI(cubeMaps);
  });

function addGUI(cubeMaps: TextureCube[]) {
  const gui = new dat.GUI();
  let colorGUI = null;
  let cubeMapGUI = null;
  let fitModeGUI = null;
  function hide(_gui) {
    _gui.__li.style.display = "none";
  }
  function show(_gui) {
    _gui.__li.style.display = "block";
  }
  background.mode = BackgroundMode.Texture;
  gui
    .add(background, "mode", {
      Sky: BackgroundMode.Sky,
      SolidColor: BackgroundMode.SolidColor,
      Texture: BackgroundMode.Texture
    })
    .onChange((v) => {
      const mode = (background.mode = parseInt(v));
      hide(colorGUI);
      hide(cubeMapGUI);
      hide(fitModeGUI);
      switch (mode) {
        case BackgroundMode.Sky:
          show(cubeMapGUI);
          break;
        case BackgroundMode.SolidColor:
          show(colorGUI);
          break;
        case BackgroundMode.Texture:
          show(fitModeGUI);
          break;
      }
    });

  const solidColor = background.solidColor;
  let colorObj = { color: [solidColor.r / 255, solidColor.g / 255, solidColor.b / 255, solidColor.a] };
  colorGUI = gui.addColor(colorObj, "color").onChange((v) => {
    background.solidColor.set(v[0] / 255, v[1] / 255, v[2] / 255, v[3] / 255);
  });

  const obj = {
    cubeMap: 0
  };

  const mode = {
    fitMode: 1
  };

  cubeMapGUI = gui.add(obj, "cubeMap", { cubeMap1: 0, cubeMap2: 1 }).onChange((v) => {
    // @ts-ignore
    background.sky.material.textureCubeMap = cubeMaps[parseInt(v)];
  });
  fitModeGUI = gui.add(mode, "fitMode", { AspectFitWidth: 0, AspectFitHeight: 1, Fill: 2 }).onChange((v) => {
    background.textureFillMode = parseInt(v);
  });

  // init
  background.mode = BackgroundMode.Texture;
  hide(colorGUI);
  hide(cubeMapGUI);
  show(fitModeGUI);
}

/**
 * @title AmbientLight
 * @category Light
 */
import * as dat from "dat.gui";
import {
  AmbientLight,
  AssetType,
  BackgroundMode,
  Camera,
  DiffuseMode,
  DirectLight,
  Logger,
  MeshRenderer,
  PBRMaterial,
  PrimitiveMesh,
  SkyBoxMaterial,
  Vector3,
  WebGLEngine
} from "oasis-engine";
import { OrbitControl } from "@oasis-engine-toolkit/controls";
Logger.enable();
const engine = new WebGLEngine("canvas");
engine.canvas.resizeByClientSize();

const scene = engine.sceneManager.activeScene;
const rootEntity = scene.createRootEntity();
const gui = new dat.GUI();

// Create camera
const cameraNode = rootEntity.createChild("camera_node");
cameraNode.transform.position = new Vector3(-3, 0, 3);
cameraNode.addComponent(Camera);
cameraNode.addComponent(OrbitControl);

// Create sky
const sky = scene.background.sky;
const skyMaterial = new SkyBoxMaterial(engine);
scene.background.mode = BackgroundMode.Sky;
sky.material = skyMaterial;
sky.mesh = PrimitiveMesh.createCuboid(engine, 1, 1, 1);

const lightEntity = rootEntity.createChild();
lightEntity.addComponent(DirectLight).intensity = 0.5;
lightEntity.transform.setPosition(-5, 5, 5);
lightEntity.transform.lookAt(new Vector3(0, 0, 0));

// material ball
const ball = rootEntity.createChild("ball");
const ballRender = ball.addComponent(MeshRenderer);
const material = new PBRMaterial(engine);
material.metallic = 0;
material.roughness = 0;
ballRender.mesh = PrimitiveMesh.createSphere(engine, 1, 64);
ballRender.setMaterial(material);

engine.resourceManager
  .load<AmbientLight>({
    type: AssetType.Env,
    url: "https://gw.alipayobjects.com/os/bmw-prod/6470ea5e-094b-4a77-a05f-4945bf81e318.bin"
  })
  .then((ambientLight) => {
    scene.ambientLight = ambientLight;
    skyMaterial.textureCubeMap = ambientLight.specularTexture;
    skyMaterial.textureDecodeRGBM = true;
    openDebug(ambientLight.specularTexture);
    engine.run();
  });

function openDebug(specularTexture) {
  const info = {
    diffuseMode: "SphericalHarmonics",
    diffuseSolidColor: [0.212 * 255, 0.227 * 255, 0.259 * 255],
    specularTexture: true
  };

  gui.add(info, "diffuseMode", ["SolidColor", "SphericalHarmonics"]).onChange((v) => {
    if (v === "SphericalHarmonics") {
      scene.ambientLight.diffuseMode = DiffuseMode.SphericalHarmonics;
    } else if (v === "SolidColor") {
      scene.ambientLight.diffuseMode = DiffuseMode.SolidColor;
    }
  });

  gui.addColor(info, "diffuseSolidColor").onChange((v) => {
    scene.ambientLight.diffuseSolidColor.set(v[0] / 255, v[1] / 255, v[2] / 255, 1);
  });

  gui.add(info, "specularTexture").onChange((v) => {
    if (v) {
      scene.ambientLight.specularTexture = specularTexture;
    } else {
      scene.ambientLight.specularTexture = null;
    }
  });

  // env light debug
  gui.add(scene.ambientLight, "specularIntensity", 0, 1);
  gui.add(scene.ambientLight, "diffuseIntensity", 0, 1);
}

/**
 * @title HDR Background
 * @category Scene
 */
import { OrbitControl } from "@oasis-engine-toolkit/controls";
import {
  AssetType,
  BackgroundMode,
  Camera,
  Logger,
  PrimitiveMesh,
  SkyBoxMaterial,
  TextureCube,
  Vector3,
  WebGLEngine
} from "oasis-engine";
Logger.enable();
const engine = new WebGLEngine("canvas");
engine.canvas.resizeByClientSize();

const scene = engine.sceneManager.activeScene;
const rootEntity = scene.createRootEntity();

// Create camera
const cameraNode = rootEntity.createChild("camera_node");
cameraNode.transform.position = new Vector3(-3, 0, 3);
const camera = cameraNode.addComponent(Camera);
cameraNode.addComponent(OrbitControl);
camera.fieldOfView = 65;

// Create sky
const sky = scene.background.sky;
const skyMaterial = new SkyBoxMaterial(engine);
scene.background.mode = BackgroundMode.Sky;
sky.material = skyMaterial;
sky.mesh = PrimitiveMesh.createCuboid(engine, 1, 1, 1);

engine.resourceManager
  .load<TextureCube>({
    type: AssetType.HDR,
    url: "https://gw.alipayobjects.com/os/bmw-prod/b578946a-8a25-4543-8161-fa92f92ae1ac.bin"
  })
  .then((texture) => {
    skyMaterial.textureCubeMap = texture;
    // HDR output is in RGBM format.
    skyMaterial.textureDecodeRGBM = true;
    engine.run();
  });
